sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below
	var fiedName= 'u_what_needed';
	var whatneeded = g_form.getValue(fiedName);
	g_form.clearOptions(fiedName);
	if(newValue == 'hr'){
		g_form.addOption(fiedName,'hr1','Human Resources 1');
		g_form.addOption(fiedName,'hr2','Human Resources 2');
		g_form.addOption(fiedName,'other','other');
	}
	if(newValue == 'facilities'){
		g_form.addOption(fiedName,'facilities1','Facilities 1');
		g_form.addOption(fiedName,'facilities2','Facilities 2');
		g_form.addOption(fiedName,'other','other');
	}

	if(newValue == 'legal'){
      g_form.addOption('u_what_needed','legal1','Legal 1');
      g_form.addOption('u_what_needed','legal2','Legal 2');
      g_form.addOption('u_what_needed','other','Other');
	}

	if(isLoading && !g_form.isNewRecord()){
		g_form.setValue(fiedName,whatneeded);
	}

}
